module.exports =  function qois(time, fn) {
    var tname = null;
    if (!fn) {        
        fn = time;
        time = 200;
    }
    return function() {
        var arg =arguments;
        tname && clearTimeout(tname);
        var self = this;
        tname = setTimeout(function() { fn.apply(self, [].slice.call(arg)); }, time);
    }
}